import React from "react";
import "./Hotel.css";
import foto1 from "./foto1.png";
import { Card, Button, Container, Col } from "react-bootstrap";

function Hotel(props) {
  return (
    <div className="container border">
      <div className="row p-2">
        <div className="col-md-3 justify-content-space">
          <img src={foto1} alt="hotel img" />
        </div>
        <div className="col-md-7 g-1">
          <div className="row g-1">
            {props.name} - {props.rate}
          </div>
          <div className="row">{props.location}</div>
          <div className="row">
            {props.oceanview == true ? "oceanview" : ""} {props.roomtype}{" "}
            {props.mealtype}
          </div>
          <div className="row">{props.price}</div>
        </div>
      </div>
    </div>
  );
}

export default Hotel;
