import React, { useState } from "react";

function NavItem(props) {
  const [is_open, set_open] = useState(false);

  return (
    <li className="nav-item">
      <a href="#" className="icon-button" onClick={() => set_open(!is_open)}>
        {props.icon}
      </a>

      {open && props.children}
    </li>
  );
}

export default NavItem;
