import React from "react";
import "./Hotel.css";
import foto1 from "./foto1.png";
import { Card, Button, Container, Col } from "react-bootstrap";

function Hotel(props) {
  return (
    <div className="container">
      <div className="row">
        <div className="col-md-3">
          <img src={foto1} alt="hotel img" />
        </div>
        <div className="col-md-7">
          <div className="row">
            {props.name} {props.rate}
          </div>
          <div className="row">{props.location}</div>
          <div className="row">
            {props.departuredate} - {props.departuredate}
          </div>
          <div className="row">
            {oceanview == true ? "oceanview" : ""}
            {roomtype}
            {mealtype}
          </div>
        </div>
      </div>
      <div className=""></div>
      <div className="hotel-description">
        <div className="hotel-description__name">
          <h3>{name}</h3>
          <p>{rate}</p>
        </div>
        <div className="hotel-desctiption__info">
          <p>{oceanview == true ? "oceanview" : ""}</p>
          <p>{roomtype}</p>
          <p>{mealtype}</p>
        </div>
        <div className="hotel-desctiption__price">
          <p>{price}</p>
        </div>
      </div>
    </div>
  );
}

export default Hotel;
