import React from "react";

function ItemHotel(props) {
  return (
    <div className="col g-6 bg-light border">
      <div className="col lg-3">
        <img src="https://picsum.photos/200" alt="hotel img" />
      </div>
      <div className="col lg-9 fs-4">
        <div className="row my-1">
          <h5 className="card-title fw-bold fs-2">
            {props.name} - {props.rate}
          </h5>
        </div>
        <div className="row my-1">
          <p className="card-text fw-light">{props.location}</p>
        </div>
        <div className="row my-1">
          <p className="card-text text-end">{props.days}</p>
        </div>
        <div className="row my-1">
          <p className="card-text text-end">
            {props.oceanview == true ? "oceanview" : ""} {props.roomtype}{" "}
            {props.mealtype}
          </p>
        </div>
        <div className="row">
          <h6 className="card-text fw-bold text-end fs-2">{props.price}</h6>
        </div>
      </div>
    </div>
  );
}

export default ItemHotel;
